mapper.update:
UPDATE
TB_ORG
SET
STATUS_CD = #{statusCd}, UPDATE_DATE = #{updateDate}, UPDATE_USER = #{updateUser}
WHERE
ORG_ID = #{orgId}

mapper.select:
SELECT COUNT(*) FROM TB_ORG A, TB_ORG_REF B, TB_ORG_REF_TYPE C WHERE A.STATUS_CD = 1000 AND B.STATUS_CD = 1000 AND C.STATUS_CD = 1000 AND A.ORG_ID = B.ORG_ID
AND B.PARENT_ORG_ID = #{org.orgId}
AND C.REF_CODE = #{org.refCode}
AND B.ORG_REF_TYPE_ID = C.ORG_REF_TYPE_ID ORDER BY A.ORG_LEVEL

SELECT A.ORG_ID orgId,A.ORG_NAME orgName,A.FULL_NAME fullName FROM TB_ORG A, TB_ORG_REF B, TB_ORG_REF_TYPE C WHERE A.STATUS_CD = 1000 AND B.STATUS_CD = 1000 AND C.STATUS_CD = 1000 AND A.ORG_ID = B.ORG_ID
AND B.ORG_ID = #{orgId}
AND C.REF_CODE = #{refCode}
AND B.ORG_REF_TYPE_ID = C.ORG_REF_TYPE_ID ORDER BY A.ORG_LEVEL

SELECT * FROM ( SELECT D.ORG_ID ORGID, D.ORG_NAME ORGNAME, D.FULL_NAME FULLNAME, D.STATUS_CD STATUSCD, D.ORG_CODE ORGCODE, D.LOC_ID LOCID, PL.LOC_CODE LOCCODE, PL.LOC_NAME LOCNAME, ( SELECT TO_CHAR(WM_CONCAT(B.ORG_TYPE_NAME)) FROM TB_ORG_ORGTYPE_REL A, TB_ORG_TYPE B WHERE A.STATUS_CD = '1000' AND B.STATUS_CD = '1000' AND A.ORG_TYPE_ID = B.ORG_TYPE_ID AND A.ORG_ID = D.ORG_ID) ORGTYPESPLIT FROM ( SELECT * FROM ( SELECT distinct B.*,G.SORT bizSort FROM TB_ORG_REL A, TB_ORG B, TB_ORG_ORGTYPE_REL F, TB_ORG_REL_TYPE C, TB_OGT_ORG_RELTYPE_CONF D, TB_ORG_TREE E, TB_ORG_ORGTREE_REL G WHERE A.STATUS_CD = '1000' AND B.STATUS_CD = '1000' AND C.STATUS_CD = '1000' AND D.STATUS_CD = '1000' AND E.STATUS_CD = '1000' AND F.STATUS_CD = '1000' AND G.STATUS_CD = '1000'
AND ${orgVo.tabOrgOrgTypeParams}
AND ${orgVo.tabOrgParams}
AND F.ORG_ID = B.ORG_ID AND E.ORG_TREE_ID = D.ORG_TREE_ID AND C.ORG_REL_TYPE_ID = D.ORG_REL_TYPE_ID AND A.REF_CODE = C.REF_CODE AND A.ORG_ID=B.ORG_ID AND A.ORG_ID=G.ORG_ID AND E.ORG_TREE_ID = G.ORG_TREE_ID AND E.ORG_TREE_ID=#{orgVo.orgTreeId} AND A.PARENT_ORG_ID=#{orgVo.orgId} ) order by bizSort) D LEFT JOIN TB_POLITICAL_LOCATION PL ON PL.LOC_ID=D.LOC_ID ) WHERE 1=1
order by #{orgVo.sortField}||' '||#{orgVo.sortOrder}

SELECT A.Org_Id orgId, A.Org_Name orgName, A.ORG_CODE orgCode, A.FULL_NAME fullName, A.STATUS_CD statusCd, to_char(A.CREATE_DATE,'YYYY-MM-DD hh24:mi:ss') createDate, DECODE(B.ORG_ORGTREE_ID,NULL,0,1) flag FROM (SELECT * FROM TB_ORG WHERE STATUS_CD = 1000
AND (ORG_NAME like '%'||#{orgVo.search}||'%' or ORG_CODE like '%'||#{orgVo.search}||'%')
) A LEFT JOIN TB_ORG_ORGTREE_REL B ON B.STATUS_CD = 1000 AND B.ORG_TREE_ID = #{orgVo.orgTreeId} AND B.ORG_ID = A.ORG_ID ORDER BY flag
