SELECT * FROM ( SELECT * FROM ( SELECT AA.ORG_ID orgId, AA.PERSONNEL_ID personnelId, AA.POST_ID postId, AA.REF_TYPE refType, AA.PROPERTY property, AA.DOUBLE_NAME doubleName, ORG.ORG_NAME orgName, ORG.Full_Name fullName, PER.PSN_NAME PsnName, PER.PSN_CODE psnCode, PER.PSN_NBR psnNbr, PER.NC_CODE ncCode, POST.POST_NAME postName, ORG_TREE_NAME orgTreeName, CTA.CONTENT mobile, AA.STATUS_CD statusCd, AA.ORG_PERSON_ID orgPersonId FROM (SELECT A.*,C.ORG_TREE_NAME FROM TB_ORG_PERSON_REL A, TB_ORGTREE_ORGPERSON_REL B ,TB_ORG_TREE C WHERE A.STATUS_CD = '1000' AND B.STATUS_CD='1000' AND C.STATUS_CD='1000' AND A.ORG_PERSON_ID=B.ORG_PERSON_ID AND B.ORG_TREE_ID=C.ORG_TREE_ID
AND C.ORG_TREE_ID = #{psonOrgVo.orgTreeId}
AND A.PERSONNEL_ID = #{psonOrgVo.personnelId}
AND A.ORG_ID = #{psonOrgVo.orgId}
) AA LEFT JOIN TB_ORG ORG ON ORG.ORG_ID = AA.ORG_ID AND ORG.Status_Cd=AA.Status_Cd LEFT JOIN TB_PERSONNEL PER ON PER.PERSONNEL_ID = AA.PERSONNEL_ID AND ORG.STATUS_CD=AA.STATUS_CD LEFT JOIN TB_POST POST ON POST.POST_ID = AA.POST_ID AND ORG.STATUS_CD=AA.STATUS_CD LEFT JOIN TB_CONTACT CTA ON CTA.PERSONNEL_ID = AA.PERSONNEL_ID AND CTA.STATUS_CD=AA.STATUS_CD AND CTA.FIRST_FLAG=1 AND CTA.CONTACT_TYPE=1 ) AB where 1=1
AND (PSNNAME LIKE '%'||#{psonOrgVo.search}||'%' OR PSNCODE LIKE '%'||#{psonOrgVo.search}||'%' OR MOBILE LIKE '%'||#{psonOrgVo.search}||'%' OR ORGNAME LIKE '%'||#{psonOrgVo.search}||'%')
)

