mapper.select:
WITH ORGTREE AS( SELECT REPLACE(REPLACE(SYS_CONNECT_BY_PATH(ORG_NAME, '->'), '->', '//'),'//', '/') FULL_NAME, ORG_NAME,ORG_ID,PARENT_ORG_ID,SORT,LOC_ID,AREA_CODE_ID,ORG_CODE,STATUS_CD FROM (SELECT A.* ,B.PARENT_ORG_ID FROM TB_ORG A, TB_ORG_REL B WHERE A.ORG_ID = B.ORG_ID AND A.STATUS_CD = 1000 AND B.STATUS_CD = 1000) AA START WITH ORG_ID = #{psonOrgVo.orgRootId} CONNECT BY PRIOR AA.ORG_ID = AA.PARENT_ORG_ID ) SELECT * FROM ( SELECT AA.ORG_ID orgId, AA.PERSONNEL_ID personnelId, AA.POST_ID postId, AA.REF_TYPE refType, AA.PROPERTY property, AA.DOUBLE_NAME doubleName, ORG.ORG_NAME orgName, (SELECT B.FULL_NAME FROM ORGTREE B WHERE B.ORG_ID=AA.ORG_ID) FULLNAME, PER.PSN_NAME PsnName, PER.PSN_CODE psnCode, PER.PSN_NBR psnNbr, PER.NC_CODE ncCode, POST.POST_NAME postName, C.ORG_TREE_NAME orgTreeName, CTA.CONTENT mobile FROM (SELECT A.* FROM TB_ORG_PERSON_REL A, TB_ORGTREE_ORGPERSON_REL B ,TB_ORG_TREE C WHERE A.STATUS_CD = '1000' AND B.STATUS_CD='1000' AND C.STATUS_CD='1000' AND A.ORG_PERSON_ID=B.ORG_PERSON_ID AND B.ORG_TREE_ID=C.ORG_TREE_ID AND C.ORG_ID = #{psonOrgVo.orgRootId}
AND A.PERSONNEL_ID = #{psonOrgVo.personnelId}
AND A.ORG_ID = #{psonOrgVo.orgId}
) AA LEFT JOIN TB_ORG ORG ON ORG.ORG_ID = AA.ORG_ID AND ORG.Status_Cd=AA.Status_Cd LEFT JOIN TB_PERSONNEL PER ON PER.PERSONNEL_ID = AA.PERSONNEL_ID AND ORG.STATUS_CD=AA.STATUS_CD LEFT JOIN TB_POST POST ON POST.POST_ID = AA.POST_ID AND ORG.STATUS_CD=AA.STATUS_CD ) AB where 1=1
AND PsnName like '%'||#{psonOrgVo.search}||'%'
LEFT JOIN TB_CONTACT CTA ON CTA.PERSONNEL_ID = AA.PERSONNEL_ID AND CTA.STATUS_CD=AA.STATUS_CD AND CTA.FIRST_FLAG=1 AND CTA.CONTACT_TYPE=1

SELECT * from ( WITH ORGTREE AS( SELECT REPLACE(REPLACE(SYS_CONNECT_BY_PATH(ORG_NAME, '->'), '->', '//'),'//', '/') FULL_NAME, ORG_NAME,ORG_ID,PARENT_ORG_ID,SORT,LOC_ID,AREA_CODE_ID,ORG_CODE,STATUS_CD FROM (SELECT A.* ,B.PARENT_ORG_ID FROM TB_ORG A, TB_ORG_REL B WHERE A.ORG_ID = B.ORG_ID AND A.STATUS_CD = 1000 AND B.STATUS_CD = 1000) AA START WITH ORG_ID = #{psonOrgVo.orgRootId} CONNECT BY PRIOR AA.ORG_ID = AA.SUP_ORG_ID ) SELECT * FROM ( SELECT AA.ORG_ID orgId, AA.PERSONNEL_ID personnelId, AA.POST_ID postId, AA.REF_TYPE refType, AA.PROPERTY property, AA.DOUBLE_NAME doubleName, ORG.ORG_NAME orgName, (SELECT B.FULL_NAME FROM ORGTREE B WHERE B.ORG_ID=AA.ORG_ID) FULLNAME, PER.PSN_NAME PsnName, PER.PSN_CODE psnCode, PER.PSN_NBR psnNbr, PER.NC_CODE ncCode, POST.POST_NAME postName, AA.ORG_TREE_NAME orgTreeName, CTA.CONTENT mobile FROM (SELECT A.*,C.Org_Tree_Name FROM TB_ORG_PERSON_REL A, TB_ORGTREE_ORGPERSON_REL B ,TB_ORG_TREE C WHERE A.STATUS_CD = '1000' AND B.STATUS_CD='1000' AND C.STATUS_CD='1000' AND A.ORG_PERSON_ID=B.ORG_PERSON_ID AND B.ORG_TREE_ID=C.ORG_TREE_ID AND C.ORG_ID = #{psonOrgVo.orgRootId} ) AA LEFT JOIN TB_ORG ORG ON ORG.ORG_ID = AA.ORG_ID AND ORG.Status_Cd=AA.Status_Cd LEFT JOIN TB_PERSONNEL PER ON PER.PERSONNEL_ID = AA.PERSONNEL_ID AND ORG.STATUS_CD=AA.STATUS_CD LEFT JOIN TB_POST POST ON POST.POST_ID = AA.POST_ID AND ORG.STATUS_CD=AA.STATUS_CD LEFT JOIN TB_CONTACT CTA ON CTA.PERSONNEL_ID = AA.PERSONNEL_ID AND CTA.STATUS_CD=AA.STATUS_CD AND CTA.FIRST_FLAG=1 AND CTA.CONTACT_TYPE=1 ) AB where 1=1
AND (PsnName like '%'||#{psonOrgVo.search}||'%' or PsnCode like '%'||#{psonOrgVo.search}||'%' or mobile like '%'||#{psonOrgVo.search}||'%')
)

