SELECT * FROM (WITH ORGFULL AS (SELECT distinct FULL_NAME, ORG_NAME, ORG_ID, PARENT_ORG_ID, SORT, LOC_ID, AREA_CODE_ID, ORG_CODE, STATUS_CD FROM (SELECT A.ORG_ID, A.SORT, A.ORG_NAME, A.LOC_ID, A.AREA_CODE_ID, A.ORG_CODE, A.STATUS_CD, A.FULL_NAME, B.PARENT_ORG_ID FROM TB_ORG A, TB_ORG_REL B, TB_ORG_TREE C, TB_OGT_ORG_RELTYPE_CONF D, TB_ORG_REL_TYPE E WHERE A.STATUS_CD = 1000 AND B.STATUS_CD = 1000 AND C.STATUS_CD = 1000 AND D.Status_Cd = 1000 and E.STATUS_CD = 1000 AND A.ORG_ID = B.ORG_ID AND B.REF_CODE = E.REF_CODE and D.ORG_REL_TYPE_ID = E.ORG_REL_TYPE_ID AND C.ORG_TREE_ID = D.ORG_TREE_ID AND C.ORG_TREE_ID = #{psonOrgVo.orgTreeId} AND B.ORG_ID = #{psonOrgVo.orgId} ) AA), acctTab as ( select t.acct_id accId, t.acct acct, t.status_cd statusCd, '1' type, '主账号' typeName, t.personnel_id personnelId, t3.psn_name psnName, t4.cert_no certNo, t5.content mobile, t.FULL_NAME orgName from (select t1.*, t2.org_id, tt.FULL_NAME from tb_acct t1, tb_account_org_rel t2, ORGFULL tt where t1.status_cd = 1000 and t2.status_cd = 1000 and t1.acct_id = t2.acct_id and t2.org_id = tt.org_id) t left join tb_personnel t3 on t.personnel_id = t3.personnel_id and t.status_cd = t3.status_cd left join tb_cert t4 on t.personnel_id = t4.personnel_id and t.status_cd = t4.status_cd left join tb_contact t5 on t.personnel_id = t5.personnel_id and t.status_cd = t5.status_cd and t5.contact_type = '1' and t5.first_flag = '1') select * from (select * from acctTab union all select t.slave_acct_id accId, t.slave_acct acct, t.status_cd statusCd, '2' type, '从账号' typeName, t.personnelId, t.psnName, t.certNo, t.mobile, t.orgName from (select t1.*, t2.personnelId, t2.psnName, t2.certNo, t2.mobile, t2.orgName from tb_slave_acct t1, acctTab t2 where t1.status_cd = 1000 and t1.acct_id = t2.accId) t) a where 1 = 1
AND (PsnName like '%'||#{psonOrgVo.search}||'%' or certNo like '%'||#{psonOrgVo.search}||'%' or mobile like '%'||#{psonOrgVo.search}||'%' or acct like '%'||#{psonOrgVo.search}||'%')
)

