SELECT * FROM ( SELECT T.ACCT_ID ACCID, T.ACCT ACCT, T.STATUS_CD STATUSCD, T.PERSONNEL_ID PERSONNELID, T.PSN_NAME PSNNAME, T.PSN_CODE PSNCODE, T.PSN_NBR PSNNBR, T.NC_CODE NCCODE, T.FULL_NAME ORGNAME, T.acct_org_rel_id acctOrgRelId, cc.slave_acct slaceAcct FROM (SELECT distinct T1.*, tbAccountOrgRel.ORG_ID,tbAccountOrgRel.acct_org_rel_id, T3.FULL_NAME, T6.PSN_NAME ,T6.PSN_CODE ,T6.PSN_NBR ,T6.NC_CODE FROM TB_ACCT T1, TB_ACCOUNT_ORG_REL tbAccountOrgRel, TB_ORG T3,TB_ORG_LEVEL T4,TB_PERSONNEL T6, TB_ORG_ORGTYPE_REL T5 WHERE T1.STATUS_CD = 1000 AND tbAccountOrgRel.STATUS_CD = 1000 AND T3.STATUS_CD = 1000 AND T4.STATUS_CD = 1000 AND T5.STATUS_CD = 1000 AND T6.STATUS_CD = 1000 AND T1.PERSONNEL_ID = T6.PERSONNEL_ID AND T1.ACCT_ID = tbAccountOrgRel.ACCT_ID
AND ${psonOrgVo.tabAccountOrgRelParams}
AND T5.ORG_ID = T3.ORG_ID AND tbAccountOrgRel.ORG_ID = T4.ORG_ID AND T4.ORG_ID=T3.ORG_ID
