AND ${psonOrgVo.tabOrgParams}
AND tbAccountOrgRel.ORG_TREE_ID = T4.ORG_TREE_ID AND T4.ORG_TREE_ID=#{psonOrgVo.orgTreeId}) T left join TB_SLAVE_ACCT cc on cc.STATUS_CD = 1000 AND cc.ACCT_ID = T.ACCT_ID and cc.acct_org_rel_id = T.acct_org_rel_id ) A WHERE 1 = 1
AND (PsnName like '%'||#{psonOrgVo.search}||'%' or acct like '%'||#{psonOrgVo.search}||'%')

