mapper.update:
UPDATE
TB_ORG_REL
SET
STATUS_CD = #{statusCd}, UPDATE_DATE = #{updateDate}, UPDATE_USER = #{updateUser}
WHERE
ORG_REL_ID = #{orgRelId}

mapper.select:
SELECT B.ORG_NAME NAME, B.ORG_ID ID ,A.PARENT_ORG_ID PID FROM TB_ORG_REL A, TB_ORG B, TB_ORG_REL_TYPE C, TB_OGT_ORG_RELTYPE_CONF D, TB_ORG_TREE E WHERE A.STATUS_CD = '1000' AND B.STATUS_CD = '1000' AND C.STATUS_CD = '1000' AND D.STATUS_CD = '1000' AND E.STATUS_CD = '1000' AND E.ORG_TREE_ID = D.ORG_TREE_ID AND C.ORG_REL_TYPE_ID = D.ORG_REL_TYPE_ID AND A.REF_CODE = C.REF_CODE AND A.ORG_ID=B.ORG_ID AND E.ORG_TREE_ID = #{orgTreeId}
AND A.PARENT_ORG_ID = #{pid}
ORDER BY B.ORG_LEVEL

SELECT ORGNAME,SUPORGNAME,ORT.REF_NAME REFNAME,ORT.CREATE_DATE createDate FROM (SELECT (SELECT O.ORG_NAME FROM TB_ORG O WHERE O.STATUS_CD = '1000' AND O.ORG_ID = B.ORG_ID) ORGNAME, (SELECT O.ORG_NAME FROM TB_ORG O WHERE O.STATUS_CD = '1000' AND O.ORG_ID = B.PARENT_ORG_ID) SUPORGNAME, B.REF_CODE FROM TB_ORG_REL B WHERE B.STATUS_CD = '1000'
AND B.ORG_ID = #{org.orgId}
) BB LEFT JOIN (SELECT * FROM TB_ORG_REL_TYPE WHERE STATUS_CD = '1000') ORT ON ORT.REF_CODE = BB.REF_CODE

SELECT ORGNAME,SUPORGNAME,ORT.REF_NAME REFNAME,ORT.ORG_BIZ_NAME orgBizName,to_char(BB.CREATE_DATE,'YYYY-MM-DD hh24:mi:ss') createDate FROM (SELECT (SELECT O.ORG_NAME FROM TB_ORG O WHERE O.STATUS_CD = '1000' AND O.ORG_ID = B.ORG_ID) ORGNAME, (SELECT O.ORG_NAME FROM TB_ORG O WHERE O.STATUS_CD = '1000' AND O.ORG_ID = B.PARENT_ORG_ID) SUPORGNAME, B.REF_CODE, B.CREATE_DATE FROM TB_ORG_REL B WHERE B.STATUS_CD = '1000'
AND B.ORG_ID = #{orgRefTypeVo.orgId}
) BB LEFT JOIN (SELECT C.REF_CODE, F.ORG_BIZ_NAME,C.REF_NAME,F.ORG_ID FROM TB_OGT_ORG_RELTYPE_CONF A, TB_ORG_TREE B, TB_ORG_REL_TYPE C ,TB_ORG_ORGTREE_REL F where A.Status_Cd=1000 AND B.STATUS_CD=1000 AND C.STATUS_CD=1000 AND F.STATUS_CD=1000 AND A.Org_Tree_Id = B.ORG_TREE_ID AND A.ORG_REL_TYPE_ID=C.ORG_REL_TYPE_ID AND B.ORG_TREE_ID=F.ORG_TREE_ID)ORT on ORT.REF_CODE=BB.REF_CODE and ORT.Org_Id=#{orgRefTypeVo.orgId}

SELECT *
FROM TB_ORG_TREE A, TB_OGT_ORG_REFTYPE_CONF B, TB_ORG_REL C
WHERE A.ORG_TREE_ID = B.ORG_TREE_ID
AND B.ORG_REF_TYPE_ID = C.ORG_REF_TYPE_ID

  AND A.ORG_TREE_ID = {#org.orgTreeId}


    AND C.ORG_ID =  {#org.orgId}

SELECT COUNT(1) FROM ( SELECT * FROM (SELECT REPLACE(REPLACE(SYS_CONNECT_BY_PATH(ORG_NAME, '->'), '->', '//'),'//', '/') fullName, ORG_NAME orgName,LEVEL lv,ORG_ID orgId FROM (SELECT A.ORG_ID, A.ORG_NAME, A.FULL_NAME, B.PARENT_ORG_ID FROM TB_ORG A, TB_ORG_REL B WHERE A.ORG_ID = B.ORG_ID AND A.STATUS_CD = 1000 AND B.STATUS_CD = 1000) AA START WITH ORG_ID = ${orgRootId} CONNECT BY PRIOR AA.ORG_ID = AA.PARENT_ORG_ID) WHERE 1=1
AND FULLNAME like '%'||#{search}||'%'
)
