mapper.select:
SELECT t.psn_name,t.psn_code,t.nc_code,
t.psn_nbr,t.nationality,t.gender,
t.nation,t.marriage,t.plitical_status,t.reason,
c.contact_type,c.content,e.cert_name,e.cert_type,
e.cert_no,e.address,e.issuing,e.is_real,e.source
FROM TB_PERSONNEL T
LEFT JOIN TB_CONTACT C ON T.PERSONNEL_ID = C.PERSONNEL_ID
LEFT JOIN TB_CERT E ON T.PERSONNEL_ID = E.PERSONNEL_ID


        AND t.psn_name = #{psnName}


        AND t.psn_nbr = #{psnNbr}


        AND e.cert_no = #{certNo}


        AND t.status_cd = #{statusCd}

select t1.personnel_id, t1.psn_name, t2.user_id, t4.double_name, t3.acct, t4.org_id, t4.status_cd from tb_personnel t1, tb_user t2, tb_acct t3, tb_acct_org t4
t1.status_cd = 1000 and t2.status_cd = 1000 and t3.status_cd = 1000 and t4.status_cd = 1000 and t1.personnel_id = t2.personnel_id and t2.user_id = t3.user_id and t3.acct_id = t4.acct_id
and t4.org_id = = #{orgId}

WITH ORGTREE AS (SELECT REPLACE(REPLACE(SYS_CONNECT_BY_PATH(ORG_NAME, '->'), '->', '//'),'//','/') FULL_NAME, ORG_NAME,ORG_ID,SUP_ORG_ID,SORT, LOC_ID, AREA_CODE_ID,ORG_CODE,STATUS_CD FROM (SELECT A.*, B.SUP_ORG_ID FROM TB_ORG A, TB_ORG_REL B WHERE A.STATUS_CD = 1000 AND B.STATUS_CD = 1000 AND A.ORG_ID = B.ORG_ID) AA START WITH ORG_ID = #{orgRootId} CONNECT BY PRIOR AA.ORG_ID = AA.SUP_ORG_ID) SELECT * FROM (SELECT AA.PROPERTY , AA.DOUBLE_NAME , AA.ORG_TREE_NAME , ORG.ORG_NAME , (SELECT B.FULL_NAME FROM ORGTREE B WHERE B.ORG_ID = AA.ORG_ID) full_name, PER.PSN_NAME , POST.POST_NAME FROM (SELECT A.*,C.Org_Tree_Name FROM TB_ORG_PERSON_REL A, TB_ORGTREE_ORGPERSON_REL B, TB_ORG_TREE C WHERE A.STATUS_CD = '1000' AND B.STATUS_CD = '1000' AND C.STATUS_CD = '1000' AND A.ORG_PERSON_ID = B.ORG_PERSON_ID AND B.ORG_TREE_ID = C.ORG_TREE_ID AND C.ORG_ID = #{orgRootId}
AND A.ORG_ID = #{orgId}
AND A.PERSONNEL_ID = #{personId}
) AA LEFT JOIN TB_ORG ORG ON ORG.ORG_ID = AA.ORG_ID AND ORG.Status_Cd = AA.Status_Cd LEFT JOIN TB_PERSONNEL PER ON PER.PERSONNEL_ID = AA.PERSONNEL_ID AND ORG.STATUS_CD = AA.STATUS_CD LEFT JOIN TB_POST POST ON POST.POST_ID = AA.POST_ID AND ORG.STATUS_CD = AA.STATUS_CD) AB where 1 = 1
