SELECT * FROM ( SELECT TB.*,ROWNUM RN FROM ( SELECT A.AREA_CODE_ID areaCodeId, A.AREA_NBR areaNbr, A.AREA_CODE areaCode, A.STATUS_CD statusCd, A.CREATE_DATE createDate,A.CREATE_USER createUser, A.UPDATE_DATE updateDate, A.UPDATE_USER updateUser,A.STATUS_DATE statusDate, X.regionNames FROM TB_AREA_CODE A LEFT JOIN ( SELECT B.AREA_CODE_ID, WM_CONCAT ( B.REGION_NAME ) AS regionNames FROM TB_COMMON_REGION B WHERE B.STATUS_CD = '1000' GROUP BY B.AREA_CODE_ID) X ON A.AREA_CODE_ID = X.AREA_CODE_ID WHERE A.STATUS_CD = '1000'
AND (A.AREA_CODE like #{keyWord} OR X.regionNames like #{keyWord} OR A.AREA_NBR like #{keyWord})
) TB WHERE ROWNUM <= #{end} ) WHERE RN >= #{from}
