mapper.select:
SELECT a.COMMON_REGION_ID, a.UP_REGION_ID, a.REGION_NAME, a.REGION_PY_NAME,a.REGION_NBR,a.REGION_TYPE, a.REGION_DESC,a.REGION_LEVEL,a.REGION_SORT,a.PROVINCE_NBR,a.CITY_FLAG, a.STATUS_CD, b.LOC_ID ,b.LOC_CODE,b.LOC_NAME FROM TB_COMMON_REGION a LEFT JOIN TB_REGION_LOCATION_REL c ON c.COMMON_REGION_ID = a.COMMON_REGION_ID LEFT JOIN TB_POLITICAL_LOCATION b ON c.LOC_ID = b.LOC_ID
and a.COMMON_REGION_ID = #{commonRegionId}
and a.STATUS_CD = #{statusCd}

SELECT a.COMMON_REGION_ID, a.UP_REGION_ID, a.REGION_NAME, a.REGION_PY_NAME,a.REGION_NBR,a.REGION_TYPE, a.REGION_DESC,a.REGION_LEVEL,a.REGION_SORT,a.PROVINCE_NBR,a.CITY_FLAG, a.STATUS_CD, b.LOC_ID ,b.LOC_CODE,b.LOC_NAME FROM TB_COMMON_REGION a LEFT JOIN TB_REGION_LOCATION_REL c ON c.COMMON_REGION_ID = a.COMMON_REGION_ID LEFT JOIN TB_POLITICAL_LOCATION b ON c.LOC_ID = b.LOC_ID
and a.COMMON_REGION_ID = #{commonRegionId}
and a.STATUS_CD = #{statusCd}
and a.UP_REGION_ID < 1
and a.UP_REGION_ID = #{upRegionId}

		SELECT
		a.COMMON_REGION_ID, a.UP_REGION_ID, a.REGION_NAME 

		FROM
		TB_COMMON_REGION a

		
			
				and a.COMMON_REGION_ID = #{commonRegionId}
			
			
				and a.STATUS_CD = #{statusCd}
			
			
				and (a.UP_REGION_ID < 1 or a.UP_REGION_ID is null)
			
			
				and a.UP_REGION_ID = #{upRegionId}
