mapper.select:
select t2.slave_acct_id, t2.slave_acct, t2.slave_acct_type, t1.host_obj_id, t1.host_obj_name from TB_SLAVE_ACCT_HOST_REL t1, tb_slave_acct t2, TB_SLVACCT_ACCT_REL t3 t1.status_cd = 1000 and t2.status_cd = 1000 and t3.status_cd = 1000 and t1.slave_acct_id = t2.slave_acct_id and t2.slave_acct_id = t3.slave_acct_id
and t3.acct_id = #{acctId}
union all select t1.slave_acct_id, t1.slave_acct, t1.slave_acct_type, null host_obj_id, '' host_obj_name from tb_slave_acct t1, TB_SLVACCT_USER_REL t2 t1.status_cd = 1000 and t2.status_cd = 1000 and t1.slave_acct_id = t2.slave_acct_id
and t2.user_id = #{userId}

WITH ORGTREE AS( SELECT REPLACE(REPLACE(SYS_CONNECT_BY_PATH(ORG_NAME, '->'), '->', '//'), '//', '/') FULL_NAME, ORG_NAME, ORG_ID, SUP_ORG_ID, SORT, LOC_ID, AREA_CODE_ID, ORG_CODE, STATUS_CD FROM (SELECT A.*, B.SUP_ORG_ID FROM TB_ORG A, TB_ORG_REL B WHERE A.ORG_ID = B.ORG_ID AND A.STATUS_CD = 1000 AND B.STATUS_CD = 1000) AA START WITH ORG_ID = #{orgRootId} CONNECT BY PRIOR AA.ORG_ID = AA.SUP_ORG_ID ), TBSLAVEACCT AS( SELECT T1.SLAVE_ACCT_ID, T1.SLAVE_ACCT, T1.SLAVE_ACCT_TYPE, T2.USER_ID FROM TB_SLAVE_ACCT T1, TB_SLVACCT_USER_REL T2 WHERE T1.STATUS_CD = 1000 AND T2.STATUS_CD = 1000 AND T1.SLAVE_ACCT_ID = T2.SLAVE_ACCT_ID UNION ALL SELECT A1.SLAVE_ACCT_ID, A1.SLAVE_ACCT, A1.SLAVE_ACCT_TYPE, A3.USER_ID FROM TB_SLAVE_ACCT A1, TB_SLVACCT_ACCT_REL A2, TB_ACCT A3 WHERE A1.STATUS_CD = 1000 AND A2.STATUS_CD = 1000 AND A3.STATUS_CD = 1000 AND A1.SLAVE_ACCT_ID = A2.SLAVE_ACCT_ID AND A2.ACCT_ID = A3.ACCT_ID ), ORGUSER AS( SELECT A.*, D.USER_ID FROM TB_ORG_PERSON_REL A, TB_ORGTREE_ORGPERSON_REL B, TB_ORG_TREE C, TB_USER D WHERE A.STATUS_CD = '1000' AND B.STATUS_CD = '1000' AND C.STATUS_CD = '1000' AND D.STATUS_CD = '1000' AND A.PERSONNEL_ID = D.PERSONNEL_ID AND A.ORG_PERSON_ID = B.ORG_PERSON_ID AND B.ORG_TREE_ID = C.ORG_TREE_ID AND C.ORG_ID = #{orgRootId}
AND A.ORG_ID = #{orgId}
) SELECT OUSER.USER_ID, PER.PSN_NAME, (SELECT B.FULL_NAME FROM ORGTREE B WHERE B.ORG_ID = OUSER.ORG_ID) FULL_NAME, (SELECT TAT.ACCT FROM TB_ACCT TAT WHERE TAT.STATUS_CD = 1000 AND TAT.USER_ID = OUSER.USER_ID) ACCT, (SELECT WM_CONCAT(TBA.SLAVE_ACCT) FROM TBSLAVEACCT TBA WHERE TBA.USER_ID = OUSER.USER_ID) SLAVE_ACCT, TCT.CONTENT, TCERT.CERT_NO, OUSER.STATUS_CD FROM ORGUSER OUSER LEFT JOIN TB_PERSONNEL PER ON PER.PERSONNEL_ID = OUSER.PERSONNEL_ID AND PER.STATUS_CD = OUSER.STATUS_CD LEFT JOIN TB_CONTACT TCT ON TCT.PERSONNEL_ID = OUSER.PERSONNEL_ID AND TCT.CONTACT_TYPE = '1' AND TCT.STATUS_CD = OUSER.STATUS_CD LEFT JOIN TB_CERT TCERT ON TCERT.PERSONNEL_ID = OUSER.PERSONNEL_ID AND TCERT.STATUS_CD = OUSER.STATUS_CD

select ROWNUM id, at.* from (select '1' type, t.acct_id, '主账号' name, t.acct,t.status_cd,to_char(t.create_date, 'yyyy-MM-dd') as create_date from TB_ACCT t where t.status_cd = 1000
and t.personnel_id = #{personnelId}
union select '2' type,t1.slave_acct_id acct_id, '从账号' name, t1.slave_acct as acct, t1.status_cd, to_char(t1.create_date, 'yyyy-MM-dd') as create_date from TB_SLAVE_ACCT t1, TB_ACCT t2 where t1.status_cd = 1000 and t2.status_cd = 1000 and t1.slave_acct_type = '1' and t1.acct_id = t2.acct_id
and t2.personnel_id = #{personnelId}
) at

select ROWNUM id, t3.*,t4.org_tree_name from (select t2.acct_org_rel_id,t2.acct_id,t1.org_id, t1.full_name, t1.org_name, t2.org_tree_id from tb_org t1, TB_account_ORG_REL t2 where t1.status_cd = 1000 and t2.status_cd = 1000 and t1.org_id = t2.org_id
and t2.acct_id = #{acctId}
) t3 left join tb_org_tree t4 on t3.org_tree_id = t4.org_tree_id and t4.status_cd = 1000

select ROWNUM id, t3.*,t4.org_tree_name from (select t2.acct_org_rel_id,t2.acct_id,t1.org_id, t1.full_name, t1.org_name, t2.org_tree_id from tb_org t1, tb_account_org_rel t2 where t1.status_cd = 1000 and t2.status_cd = 1000 and t1.org_id = t2.org_id
and t2.ACCT_ORG_REL_ID = #{acctOrgVo.acctOrgRelId}
) t3 left join tb_org_tree t4 on t3.org_tree_id = t4.org_tree_id and t4.status_cd = 1000
