mapper.select:
select T.ORG_TYPE_ID id, T.SUP_PK_ORG_TYPE pid, T.ORG_TYPE_NAME name from tb_org_type T where T.STATUS_CD = 1000
AND T.SUP_PK_ORG_TYPE = #{supOrgTypeId}
ORDER BY T.ORG_TYPE_ID ASC

select T.ORG_TYPE_ID id, T.SUP_PK_ORG_TYPE pid, T.ORG_TYPE_NAME name from tb_org_type T where T.STATUS_CD = 1000
AND T.SUP_PK_ORG_TYPE = #{supOrgTypeId}
ORDER BY T.ORG_TYPE_ID ASC
